// Function to Display Recipe Book
function displayRecipeBook() {
    const recipeBookContainer = document.getElementById("recipeBookContainer");
    
    recipeBook.forEach(recipe => {
        const recipeCard = document.createElement("div");
        recipeCard.classList.add("recipe-card");
        
        recipeCard.innerHTML = `
            <img src="${recipe.image}" alt="${recipe.title}" class="recipe-image">
            <h3>${recipe.title}</h3>
            <p><strong>Ingredients:</strong> ${recipe.ingredients.join(", ")}</p>
            <p><strong>Instructions:</strong> ${recipe.instructions.substring(0, 100)}...</p>
            <a href="javascript:void(0);" onclick="viewRecipeDetails(${JSON.stringify(recipe)})">View Full Recipe</a>
        `;
        
        recipeBookContainer.appendChild(recipeCard);
    });
}

// Function to View Full Recipe in Modal
function viewRecipeDetails(recipe) {
    const modal = document.createElement("div");
    modal.classList.add("modal");
    
    modal.innerHTML = `
        <div class="modal-content">
            <span class="close-button" onclick="closeModal()">&times;</span>
            <h2>${recipe.title}</h2>
            <img src="${recipe.image}" alt="${recipe.title}" class="recipe-image">
            <h3>Ingredients</h3>
            <ul>
                ${recipe.ingredients.map(ingredient => `<li>${ingredient}</li>`).join('')}
            </ul>
            <h3>Instructions</h3>
            <p>${recipe.instructions}</p>
        </div>
    `;
    
    document.body.appendChild(modal);
}

// Close the modal
function closeModal() {
    const modal = document.querySelector(".modal");
    modal.remove();
}

// Function to Find Recipe Based on Ingredients (Using API)
function findRecipe() {
    const ingredients = document.getElementById("ingredients").value;
    const apiKey = 'your_api_key_here'; // Replace with your actual API key
    const url = `https://api.spoonacular.com/recipes/findByIngredients?ingredients=${ingredients}&number=5&apiKey=${apiKey}`;
    
    fetch(url)
        .then(response => response.json())
        .then(data => {
            const recipeSection = document.getElementById("recipe");
            recipeSection.innerHTML = ''; // Clear previous results

            data.forEach(recipe => {
                const recipeCard = document.createElement("div");
                recipeCard.classList.add("recipe-card");
                
                recipeCard.innerHTML = `
                    <img src="https://spoonacular.com/recipeImages/${recipe.id}-480x360.jpg" alt="${recipe.title}" class="recipe-image">
                    <h3>${recipe.title}</h3>
                    <p><strong>Ingredients:</strong> ${recipe.ingredients.slice(0, 5).join(", ")}</p>
                    <a href="https://spoonacular.com/recipes/${recipe.title.replace(/ /g, '-')}-${recipe.id}" target="_blank">View Full Recipe</a>
                `;
                
                recipeSection.appendChild(recipeCard);
            });
        })
        .catch(error => console.error('Error fetching data:', error));
}

// Display static recipe book on page load
displayRecipeBook();
